const contenedor = document.getElementById("contenedor");

for (let libro of books) {

    const div = document.createElement("div")

    div.innerHTML = ` 
<div class="card mb-5 eos-init" style="max-width: 1200px; min-width: 865px;" data-aos="zoom-in" data-aos-delay="175" data-aos-duration="1500">
<div class="row g-0 ">
    <div class="col-md-4">
    <a target="_blank" href="${libro.pag}">
        <img src="${libro.img}" class="img-fluid rounded-start imgCards" alt="${libro.nombre}.img">
    </a>
    </div>
    <div class="col-md-8">
        <div class="card-body letraAltona">
            <h5 class="card-title tituloCard " style="font-width:"1000";">${libro.nombre}</h5>
            <p class="card-text h4 m-4">${libro.genero}</p>
            <p class="card-text h5">${libro.descrip}</p>
            <p class="card-text h5"><small class="text-muted"></small></p>
        </div>
    </div>
</div>
</div>`;
    
    contenedor.append(div)

}
window.addEventListener("scroll", function(){
    let header = document.querySelector("header");
    header.classList.toggle("abajo", window.scrollY>0)
})
const contenedor2 =document.getElementById("contenedor2");
const btnBio = document.getElementById("btnBio")

function crearBtnBio () {
    
    let div = document.createElement("div");
    div.innerHTML = `
    <div class="d-flex justify-content-center">
        <div class="card-bio p-4 m-4" data-aos="flip-left"
        data-aos-easing="ease-out-cubic"
        data-aos-duration="2000">
        <h1 class="h4 letraAltona" style="color: "white"; line-height: 30%; font-size:35px;">${biografia}</h1>
        </div>
    </div>
        `;
    contenedor2.append(div)
}
btnBio.addEventListener("click", () => {
    let i = 0;
    if (i === 0){
        crearBtnBio();
        window.scrollTo(0, 6255);
        i++;
    } else {
        window.scrollTo(0, 6255);
    }
})

